window.addEventListener("load",(function(){var t=document.getElementsByClassName("faq");function e(){status=this.getAttribute("data-status");let t=this.querySelector(".faq__desc");"close"==status?(t.classList.remove("hidden"),this.setAttribute("data-status","open")):(t.classList.add("hidden"),this.setAttribute("data-status","close"))}for(let s=0;s<t.length;s++)t[s].addEventListener("click",e)}));